import React, { useState, useEffect, useCallback } from "react";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid } from "recharts";
import "./App.css";

function App() {
  const [symbol, setSymbol] = useState("RELIANCE.NS");
  const [data, setData] = useState([]);
  const [info, setInfo] = useState({});
  const [loading, setLoading] = useState(false);

  // âœ… useCallback prevents ESLint warning and stabilizes function
  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:5001/api/stock/${symbol}`);
      const result = await response.json();

      if (result.error) {
        console.error(result.error);
        return;
      }

      setData(result.trend);
      setInfo({
        current: result.current,
        high: result.high,
        low: result.low,
      });
    } catch (error) {
      console.error("Fetch error:", error);
    } finally {
      setLoading(false);
    }
  }, [symbol]); // depends only on the stock symbol

  // ðŸ•’ Auto-refresh every 10 seconds
  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 10000);
    return () => clearInterval(interval);
  }, [fetchData]); // now ESLint is happy ðŸŽ‰

  return (
    <div className="App">
      <h1>ðŸ“Š PrediTradeAI Dashboard</h1>

      <div className="input-section">
        <input
          type="text"
          value={symbol}
          onChange={(e) => setSymbol(e.target.value.toUpperCase())}
          placeholder="Enter Stock Symbol (e.g. RELIANCE.NS)"
        />
        <button onClick={fetchData}>Fetch Data</button>
      </div>

      <div className="info-section">
        <p>ðŸ’° Current: â‚¹{info.current?.toFixed(2)}</p>
        <p>ðŸ“ˆ High: â‚¹{info.high?.toFixed(2)}</p>
        <p>ðŸ“‰ Low: â‚¹{info.low?.toFixed(2)}</p>
      </div>

      {loading ? (
        <p>Loading live data...</p>
      ) : (
        <LineChart
          width={800}
          height={400}
          data={data}
          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid stroke="#333" />
          <XAxis dataKey="time" />
          <YAxis domain={["auto", "auto"]} />
          <Tooltip />
          <Line type="monotone" dataKey="price" stroke="#00ffcc" strokeWidth={2} />
        </LineChart>
      )}
    </div>
  );
}

export default App;
