import React, { useState, useEffect } from "react";
import axios from "axios";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip } from "recharts";
import "./App.css";

function App() {
  const [symbol, setSymbol] = useState("RELIANCE.NS");
  const [data, setData] = useState([]);
  const [info, setInfo] = useState(null);

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/live/${symbol}`);
      const chart = response.data.chart;
      if (chart && chart.timestamp && chart.indicators) {
        const timestamps = chart.timestamp;
        const prices = chart.indicators.quote[0].close;
        const formatted = timestamps.map((t, i) => ({
          date: new Date(t * 1000).toLocaleDateString(),
          price: prices[i],
        }));
        setData(formatted);
        setInfo({
          current: prices[prices.length - 1],
          high: Math.max(...prices),
          low: Math.min(...prices),
        });
      }
    } catch (error) {
      console.error("âŒ Error fetching stock data:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="app">
      <h1>ðŸ“Š PrediTradeAI Dashboard</h1>
      <div className="input-section">
        <input
          value={symbol}
          onChange={(e) => setSymbol(e.target.value)}
          placeholder="Enter Stock Symbol (e.g. TCS.NS)"
        />
        <button onClick={fetchData}>Fetch Data</button>
      </div>

      {info && (
        <div className="stats">
          <p>ðŸ’° Current: â‚¹{info.current.toFixed(2)}</p>
          <p>ðŸ“ˆ High: â‚¹{info.high.toFixed(2)}</p>
          <p>ðŸ“‰ Low: â‚¹{info.low.toFixed(2)}</p>
        </div>
      )}

      {data.length > 0 ? (
        <LineChart
          width={800}
          height={400}
          data={data}
          margin={{ top: 20, right: 30, left: 20, bottom: 10 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis domain={["auto", "auto"]} />
          <Tooltip />
          <Line type="monotone" dataKey="price" stroke="#00E676" strokeWidth={2} />
        </LineChart>
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
}

export default App;
