import React, { useEffect, useMemo, useState } from "react";
import Navbar from "../components/Navbar";
import StockChart from "../components/StockChart";
import StockCard from "../components/StockCard";
import PredictionCard from "../components/PredictionCard";
import { SAMPLE_STOCKS } from "../data/sampleData";
import { FiSearch } from "react-icons/fi";

/* ---- Simple AI Estimator ---- */
function estimateNext(series, lookback = 8) {
  if (!series || series.length < 3)
    return { predictedPrice: null, recommendation: "Hold", pctChange: null };

  const n = Math.min(lookback, series.length);
  const xs = [],
    ys = [];
  for (let i = series.length - n; i < series.length; i++) {
    xs.push(i);
    ys.push(series[i].price);
  }
  const xMean = xs.reduce((a, b) => a + b, 0) / xs.length;
  const yMean = ys.reduce((a, b) => a + b, 0) / ys.length;
  let num = 0,
    den = 0;
  for (let i = 0; i < xs.length; i++) {
    num += (xs[i] - xMean) * (ys[i] - yMean);
    den += (xs[i] - xMean) ** 2;
  }
  const slope = den === 0 ? 0 : num / den;
  const pred = series[series.length - 1].price + slope;
  const pct =
    ((pred - series[series.length - 1].price) /
      series[series.length - 1].price) *
    100;

  let rec = "Hold";
  if (pct > 0.6) rec = "Buy";
  if (pct < -0.6) rec = "Sell";

  return {
    predictedPrice: parseFloat(pred.toFixed(2)),
    recommendation: rec,
    pctChange: parseFloat(pct.toFixed(2)),
  };
}

/* ---- AI Reasoning Generator ---- */
function generateAIReasoning(rec, pctChange) {
  const base = {
    Buy: [
      "Momentum looks strong — upward slope detected in recent trend.",
      "Increased volume and steady climb suggest accumulation phase.",
      "RSI under 60 and moving averages aligning bullishly.",
    ],
    Sell: [
      "Short-term correction likely — price overextended recently.",
      "Indicators show negative divergence on RSI and MACD.",
      "Resistance forming near recent highs; profit booking phase.",
    ],
    Hold: [
      "Market moving sideways — best to wait for confirmation.",
      "No major volume spikes or trend breaks detected.",
      "Stable zone; volatility low, risk balanced.",
    ],
  };

  const randomReason =
    base[rec][Math.floor(Math.random() * base[rec].length)];
  const confidence = Math.floor(Math.random() * 15 + 80); // 80–95%

  return { reason: randomReason, confidence };
}

/* ---- Main Dashboard ---- */
export default function Dashboard() {
  const user = JSON.parse(localStorage.getItem("pt_user"))?.email ?? "Guest";

  const [stockKey, setStockKey] = useState("RELIANCE");
  const [range, setRange] = useState(30);
  const [query, setQuery] = useState("");
  const [series, setSeries] = useState(SAMPLE_STOCKS.RELIANCE);
  const [aiInsight, setAiInsight] = useState({ reason: "", confidence: 0 });

  useEffect(() => {
    const s =
      SAMPLE_STOCKS[stockKey] ||
      (() => {
        const gen = require("../data/sampleData").generateSeries;
        return gen
          ? gen(90, 300 + Math.random() * 1000, (Math.random() - 0.5) * 0.002)
          : SAMPLE_STOCKS.RELIANCE;
      })();
    setSeries(s);
  }, [stockKey]);

  const sliced = useMemo(
    () => series.slice(Math.max(0, series.length - range)),
    [series, range]
  );

  const prices = sliced.map((d) => d.price);
  const high = Math.max(...prices).toFixed(2);
  const low = Math.min(...prices).toFixed(2);
  const avg = (prices.reduce((a, b) => a + b, 0) / prices.length).toFixed(2);
  const latest = prices[prices.length - 1].toFixed(2);
  const changePct = (((latest - prices[0]) / prices[0]) * 100).toFixed(2);

  const prediction = estimateNext(series, 8);

  useEffect(() => {
    setAiInsight(generateAIReasoning(prediction.recommendation, prediction.pctChange));
  }, [prediction]);

  function handleSearch(e) {
    e.preventDefault();
    const key = query.toUpperCase().replace(/[^A-Z0-9]/g, "");
    if (!key) return;
    if (SAMPLE_STOCKS[key]) {
      setStockKey(key);
      setQuery("");
    } else {
      SAMPLE_STOCKS[key] = (() => {
        const arr = [];
        let start = 300 + Math.random() * 2000;
        for (let i = 89; i >= 0; i--) {
          const d = new Date();
          d.setDate(d.getDate() - i);
          start = Math.max(1, start * (1 + (Math.random() - 0.48) * 0.006));
          arr.push({
            date: `${d.getDate()}/${d.getMonth() + 1}`,
            price: parseFloat(start.toFixed(2)),
            volume: Math.floor(5000 + Math.random() * 90000),
          });
        }
        return arr;
      })();
      setStockKey(key);
      setQuery("");
    }
  }

  return (
    <div className="app-shell">
      <Navbar user={user} />
      <main className="main-grid">
        <section className="panel left">
          {/* --- Search --- */}
          <div className="search-row">
            <form onSubmit={handleSearch} className="search-box">
              <FiSearch className="search-icon" />
              <input
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Search ticker (e.g. RELIANCE, TCS)"
              />
              <button className="btn small" type="submit">
                Search
              </button>
            </form>

            <div className="range-group">
              <label>Range</label>
              <select
                value={range}
                onChange={(e) => setRange(parseInt(e.target.value))}
              >
                <option value={7}>7d</option>
                <option value={14}>14d</option>
                <option value={30}>30d</option>
                <option value={60}>60d</option>
                <option value={90}>90d</option>
              </select>
            </div>
          </div>

          {/* --- Chart --- */}
          <div className="chart-card">
            <div className="chart-head">
              <div>
                <h2 className="stock-name">{stockKey}</h2>
                <div className="stock-meta">
                  Latest: <span className="meta-strong">₹{latest}</span> ·{" "}
                  {changePct}% ({range}d)
                </div>
              </div>
              <div className="mini-stats">
                <div
                  className={`trend-pill ${prediction.recommendation?.toLowerCase()}`}
                >
                  {prediction.recommendation}{" "}
                  {prediction.pctChange >= 0
                    ? `+${prediction.pctChange}%`
                    : `${prediction.pctChange}%`}
                </div>
                <div className="next-est">
                  Next est: ₹{prediction.predictedPrice}
                </div>
              </div>
            </div>

            <StockChart data={sliced} />
          </div>

          {/* --- Stats --- */}
          <div className="stats-row">
            <StockCard
              title="High (range)"
              value={high}
              foot="Max in selected window"
              positive={true}
            />
            <StockCard
              title="Low (range)"
              value={low}
              foot="Min in selected window"
              positive={false}
            />
            <StockCard title="Average" value={avg} foot="Mean price" />
          </div>
        </section>

        {/* --- Right Panel --- */}
        <aside className="panel right">
          <div className="side-card">
            <h3>Daily Overview</h3>
            <div className="overview-item">
              <div>Open</div>
              <div>₹{sliced[0]?.price ?? latest}</div>
            </div>
            <div className="overview-item">
              <div>Close</div>
              <div>₹{latest}</div>
            </div>
            <div className="overview-item">
              <div>Change</div>
              <div className={changePct >= 0 ? "pos" : "neg"}>{changePct}%</div>
            </div>
            <div className="overview-item">
              <div>Volume (last)</div>
              <div>{sliced[sliced.length - 1]?.volume ?? "—"}</div>
            </div>
          </div>

          {/* --- AI Prediction --- */}
          <PredictionCard
            predictedPrice={prediction.predictedPrice}
            recommendation={prediction.recommendation}
            pctChange={prediction.pctChange}
          />

          {/* --- AI Insight Panel --- */}
          <div className="prediction-panel">
            <div className="pred-head">
              <h3>AI Insight</h3>
              <span
                className={`pred-badge ${
                  prediction.recommendation?.toLowerCase()
                }`}
              >
                {prediction.recommendation}
              </span>
            </div>
            <div className="pred-body">
              <div className="pred-row">
                <span className="pred-label">Confidence Level:</span>
                <span className="pred-value">{aiInsight.confidence}%</span>
              </div>
              <div className="pred-row">
                <span className="pred-label">AI Reasoning:</span>
                <span className="pred-value">{aiInsight.reason}</span>
              </div>
              <button
                className="btn ghost block"
                onClick={() =>
                  setAiInsight(
                    generateAIReasoning(
                      prediction.recommendation,
                      prediction.pctChange
                    )
                  )
                }
              >
                🔁 Re-analyze with AI
              </button>
            </div>
          </div>

          <div className="side-card">
            <h3>Quick Actions</h3>
            <button className="btn primary block">Simulate Buy</button>
            <button className="btn ghost block">Simulate Sell</button>
            <button
              className="btn outline block"
              onClick={() => {
                alert("Export CSV (demo) — implement server/export logic");
              }}
            >
              Export CSV
            </button>
          </div>

          <div className="footer-note">
            <small>
              🤖 AI module simulates logical predictions. Replace estimator with
              a trained ML model API for production.
            </small>
          </div>
        </aside>
      </main>
    </div>
  );
}
