import React, { useState, useEffect } from "react";
import axios from "axios";
import StockChart from "../components/StockChart";
import "../App.css";

function Dashboard() {
  const [symbol, setSymbol] = useState("RELIANCE");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchStock = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:5001/api/stock/${symbol}`);
      setData(res.data);
    } catch (error) {
      console.error("Error fetching stock data:", error.message);
    }
    setLoading(false);
  };

  useEffect(() => {
  fetchStock();
  // eslint-disable-next-line react-hooks/exhaustive-deps
}, []);


  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">ðŸ“Š PrediTradeAI Dashboard</h1>

      <div className="search-section">
        <input
          value={symbol}
          onChange={(e) => setSymbol(e.target.value.toUpperCase())}
          placeholder="Enter Stock Symbol (e.g. TCS)"
        />
        <button onClick={fetchStock}>Fetch Data</button>
      </div>

      {loading && <p>Loading...</p>}

      {data && (
        <div className="stock-details">
          <h2>{data.name}</h2>
          <p>ðŸ’° Current: â‚¹{data.current.toFixed(2)}</p>
          <p>ðŸ“ˆ High: â‚¹{data.high.toFixed(2)}</p>
          <p>ðŸ“‰ Low: â‚¹{data.low.toFixed(2)}</p>
          <StockChart data={data.trendData} />
        </div>
      )}
    </div>
  );
}

export default Dashboard;
