// src/data/sampleData.js

// Sample stock data for demo
export const SAMPLE_STOCKS = [
  { name: "AAPL", price: 178.23, change: 1.23 },
  { name: "GOOGL", price: 135.76, change: -0.45 },
  { name: "TSLA", price: 245.11, change: 2.1 },
  { name: "MSFT", price: 325.88, change: 0.74 },
];

// ✅ This function generates fake time series data for AI/Prediction charts
export const generateSeries = (length = 20) => {
  const data = [];
  let base = 100 + Math.random() * 50;
  for (let i = 0; i < length; i++) {
    const change = (Math.random() - 0.5) * 5;
    base += change;
    data.push({
      name: `Day ${i + 1}`,
      value: Number(base.toFixed(2)),
    });
  }
  return data;
};
