import React, { useState, useEffect } from "react";
import "./App.css";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";

function App() {
  const [symbol, setSymbol] = useState("RELIANCE.NS");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchData = async () => {
    try {
      setLoading(true);
      setError("");
      const response = await fetch(`http://127.0.0.1:5000/api/live/${symbol}`);
      if (!response.ok) {
        throw new Error("Stock data not found");
      }
      const result = await response.json();
      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [symbol]);

  return (
    <div className="app">
      <header className="header">
        <h1>üíπ PrediTradeAI ‚Äî Live Stock Dashboard</h1>
      </header>

      <div className="dashboard">
        <div className="controls">
          <input
            type="text"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value.toUpperCase())}
            placeholder="Enter Stock Symbol (e.g., RELIANCE.NS)"
            className="input"
          />
          <button onClick={fetchData} className="btn">Fetch Data</button>
        </div>

        {loading && <p className="info">Fetching live data...</p>}
        {error && <p className="error">‚ö†Ô∏è {error}</p>}

        {!loading && data.length > 0 && (
          <div className="chart-container">
            <h2>{symbol} ‚Äî Live Price Movement</h2>
            <LineChart
              width={900}
              height={400}
              data={data}
              margin={{ top: 20, right: 40, left: 0, bottom: 20 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis domain={["auto", "auto"]} />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="price" stroke="#007bff" name="Live Price" />
            </LineChart>
          </div>
        )}

        {!loading && !data.length && !error && (
          <p className="info">No data to display. Try fetching a stock symbol above.</p>
        )}
      </div>

      <footer className="footer">
        <p>Built with ‚ù§Ô∏è by Divjot | Data Science Dashboard</p>
      </footer>
    </div>
  );
}

export default App;
