import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import "./App.css";

function App() {
  const [stockData, setStockData] = useState([]);
  const [symbol, setSymbol] = useState("RELIANCE.NS");

  // Mock data (so it always shows charts)
  const generateDemoData = () => {
    const data = [];
    for (let i = 1; i <= 20; i++) {
      data.push({
        date: `2025-10-${i < 10 ? "0" + i : i}`,
        price: (Math.random() * 2000 + 2400).toFixed(2),
      });
    }
    return data;
  };

  // Fetch live data (or fallback to demo)
  useEffect(() => {
    fetch(`http://127.0.0.1:5001/api/live/${symbol}`)
      .then((res) => res.json())
      .then((data) => {
        if (data && data.length > 0) setStockData(data);
        else setStockData(generateDemoData());
      })
      .catch(() => setStockData(generateDemoData()));
  }, [symbol]);

  return (
    <div className="app-container">
      <h1 className="title">ðŸ“Š Preditrade AI Dashboard</h1>

      <div className="input-container">
        <input
          className="symbol-input"
          value={symbol}
          onChange={(e) => setSymbol(e.target.value.toUpperCase())}
          placeholder="Enter stock symbol (e.g., RELIANCE.NS)"
        />
      </div>

      <div className="chart-section">
        <h2>Stock Price Trends</h2>
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={stockData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip />
            <Line
              type="monotone"
              dataKey="price"
              stroke="#4CAF50"
              strokeWidth={2}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>

      <div className="info-card">
        <h3>AI Insights</h3>
        <p>
          This dashboard analyzes live and historical market trends using AI-driven
          data modeling. You can later connect backend prediction APIs here.
        </p>
      </div>
    </div>
  );
}

export default App;
